        if direction == 'forward':
            if r > 0 and maze[r-1][c] == '/':
                count += 1
            if r < R - 1 and maze[r+1][c] == '\\':
                count += 1
        else:  # backward
            if r > 0 and maze[r-1][c] == '\\':
                count += 1
            if r < R - 1 and maze[r+1][c] == '/':
                count += 1
        return count

    odd_squares = []
    even_squares = []

    for r in range(R):
        for c in range(C):
            if maze[r][c] == '/':
                if is_odd_square(r, c):
                    odd_squares.append((r, c))
                else:
                    even_squares.append((r, c))
            else:  # maze[r][c] == '\\'
                if is_odd_square(r, c):
                    even_squares.append((r, c))
                else:
                    odd_squares.append((r, c))

    forward_walls = 0
    backward_walls = 0

    for r, c in odd_squares:
        forward_walls += count_walls(r, c, 'forward')
        backward_walls += count_walls(r, c, 'backward')

    for r, c in even_squares:
        forward_walls += count_walls(r, c, 'forward')
        backward_walls += count_walls(r, c, 'backward')

    return min(forward_walls, backward_walls)

