n = int(input())
tree = [[] for _ in range(n + 1)]
for _ in range(n - 1):
    u, v = map(int, input().split())
    tree[u].append(v)
    tree[v].append(u)

def dfs(node, par):
    children = []
    for child in tree[node]:
        if child != par:
            children.append(dfs(child, node))
    if not children:
        return [node]
    paths = []
    for path in children:
        paths.append([node] + path)
    return paths[0] + paths[1][::-1]

