            return False

        current_height = heights[y][x]

        for dx in [-1, 0, 1]:
            for dy in [-1, 0, 1]:
                if dx == 0 and dy == 0:
                    continue

                nx, ny = x + dx, y + dy

                if 0 <= nx < W and 0 <= ny < H:
                    neighbor_height = heights[ny][nx]
                    if abs(current_height - neighbor_height) > 1:
                        return False

        return True

    def find_max_distance(start_x, start_y, current_t, current_distance):
        max_distance = current_distance

        for dx in [-1, 0, 1]:
            for dy in [-1, 0, 1]:
                if dx == 0 and dy == 0:
                    continue

                nx, ny = start_x + dx, start_y + dy

                if 0 <= nx < W and 0 <= ny < H:
                    next_t = current_t + 0.5  # Assuming the tide takes about 0.5 hours to cross a 10x10m square

                    if 0.0 <= next_t <= 12.0:
                        next_distance = math.sqrt((nx - start_x)**2 + (ny - start_y)**2)

                        if is_safe(nx, ny, next_t):
                            max_distance = max(max_distance, find_max_distance(nx, ny, next_t, current_distance + next_distance))

        return max_distance

