    n, d = map(int, input().split())
    a = list(map(int, input().split()))
    m = int(input())

    fares = []
    for _ in range(m):
        s, d, t, p = input().split()
        p = int(p)
        fares.append((int(s), int(d), t, p))

    dp = {}

    def calculate_cost(start, end, remaining_concerts, path):
        if (start, end, remaining_concerts, tuple(path)) in dp:
            return dp[(start, end, remaining_concerts, tuple(path))]

        if remaining_concerts == 0:
            return 0

        if start != end:
            min_cost = float('inf')

            for s, d, t, price in fares:
                if start == s and d == end and t == 'O':
                    min_cost = min(min_cost, price + calculate_cost(start, end, remaining_concerts - 1, path + [d]))
                if start == s and d == end and t == 'R':
                    min_cost = min(min_cost, price + calculate_cost(start, d, remaining_concerts - 2, path + [d]))
                    min_cost = min(min_cost, price + calculate_cost(d, start, remaining_concerts - 2, path + [start]))
            return min_cost

        else:
            min_cost = float('inf')
            for s, d, t, price in fares:
                if start == s and d == end and t == 'O':
                    min_cost = min(min_cost, price)
                if start == s and d == end and t == 'R':
                    min_cost = min(min_cost, 2 * price)
            return min_cost

    print(calculate_cost(a[0], a[d-1], d - 1, [a[0]]))

