def solve():
    n, m, alpha = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v, c = map(int, input().split())
        edges.append((u, v, c))

    def calculate_energy(path):
        l = max(c for u, v, c in path)
        k = len(path)
        return l**2 + alpha * k

    def find_all_paths(start_node):
        paths = []
        def dfs(current_node, current_path, visited):
            nonlocal paths
            if current_node == start_node and len(current_path) > 1:
                paths.append(current_path[:])
                return

            visited[current_node] = True
            for u, v, c in edges:
                if u == current_node and not visited[v]:
                    new_path = current_path + [(u, v, c)]
                    dfs(v, new_path, visited.copy())
                elif v == current_node and not visited[u]:
                    new_path = current_path + [(v, u, c)]
                    dfs(u, new_path, visited.copy())
        visited = {i: False for i in range(1, n + 1)}
        dfs(start_node, [(start_node, start_node, 0)], visited)
        return paths

    min_energy = float('inf')
    for start_node in range(1, n + 1):
        for end_node in range(1, n + 1):
            paths = find_all_paths(start_node)
            for path in paths:
                if path[0][0] == start_node and path[-1][1] == end_node:
                    min_energy = min(min_energy, calculate_energy(path))
    if min_energy == float('inf'):
        print("Poor girl")
    else:
        print(min_energy)

