def solve():
    n, m, b = map(int, input().split())
    target = []
    for _ in range(b):
        target.append(tuple(map(int, input().split())))
    target = tuple(target)

    initial = {(target[0][0], target[0][1])}
    edges = {}
    for r, c in target[1:]:
        edges[(r, c)] = []
        for nr, nc in [(r-1, c), (r+1, c), (r, c-1), (r, c+1)]:
            if 1 <= nr <= n and 1 <= nc <= m and (nr, nc) in initial:
                edges[(r, c)].append((nr, nc))

    def get_moves(current, target):
        if current == target:
            return []

        possible_moves = []

        # Check right edge
        if current[0][1] < m:
            new_current = tuple(sorted(current + {(current[0][0], current[0][1] + 1)}))
            if new_current not in visited:
                visited.add(new_current)
                moves = get_moves(new_current, target)
                if moves:
                    possible_moves.append((">", 1) + moves[0])

        # Check left edge
        if current[0][1] > 1:
            new_current = tuple(sorted(current + {(current[0][0], current[0][1] - 1)}))
            if new_current not in visited:
                visited.add(new_current)
                moves = get_moves(new_current, target)
                if moves:
                    possible_moves.append(("<", 1) + moves[0])

        # Check top edge
        if current[0][0] > 1:
            new_current = tuple(sorted(current + {(current[0][0] - 1, current[0][1]})))
            if new_current not in visited:
                visited.add(new_current)
                moves = get_moves(new_current, target)
                if moves:
                    possible_moves.append(("^", 1) + moves[0])

        # Check bottom edge
        if current[0][0] < n:
            new_current = tuple(sorted(current + {(current[0][0] + 1, current[0][1]})))
            if new_current not in visited:
                visited.add(new_current)
                moves = get_moves(new_current, target)
                if moves:
                    possible_moves.append(("v", 1) + moves[0])

        return possible_moves

    visited = set()
    visited.add(current)
    moves = get_moves(current, target)

    if not moves:
        print("impossible")
        return

    print("possible")
    for move, k in moves:
        print(move, k)

