def solve():
    n = int(input())
    nums = []
    for _ in range(n):
        nums.append(int(input()))

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def count_factors(num):
        count = 0
        i = 1
        while i * i <= num:
            if num % i == 0:
                if i * i == num:
                    count += 1
                else:
                    count += 2
            i += 1
        return count

    def is_valid(arr):
        for i in range(len(arr)):
            for j in range(i + 1, len(arr)):
                if gcd(arr[i], arr[j]) == 1:
                    return False
        return True

    def count_ways(arr, index, path, visited):
        if index == 0:
            if is_valid(path):
                return 1
            else:
                return 0

        count = 0
        for i in range(index + 1, len(arr)):
            if not visited[i]:
                visited[i] = True
                path.append(arr[i])
                count += count_ways(arr, i, path, visited)
                path.pop()
                visited[i] = False

        return count

    import itertools
    perms = list(itertools.permutations(nums))
    ans = 0
    for perm in perms:
        visited = [False] * n
        visited[0] = True
        path = [perm[0]]
        ans = (ans + count_ways(list(perm), 0, path, visited)) % (10**9 + 7)
    print(ans)

solve()

def solve():
    k = int(input())
    a = list(map(int, input().split()))
    q = int(input())

    def xorbonacci(n, k, a):
        if n <= k:
            return a[n-1]

        dp = [0] * (n + 1)
        dp[:k] = a[:k]

        for i in range(k, n + 1):
            dp[i] = 0
            for j in range(k):
                if i - j >= k:
                    dp[i] ^= dp[i - j - 1]

        return dp[n]

    for _ in range(q):
        l, r = map(int, input().split())
        l, r = l - 1, r

        ans = xorbonacci(r, k, a)
        if l > 0:
            ans ^= xorbonacci(l - 1, k, a)

        print(ans)

