        if winner_points == 0 and loser_points > 0:
            return 1.0
        if winner_points > 0 and loser_points == 0:
            return 0.0

        total_probability = 0.0
        for i in range(len(probabilities)):
            if probabilities[i] > 0:
                new_winner_points = winner_points - 1
                new_loser_points = loser_points
                if new_winner_points > 0:
                    total_probability += probabilities[i] * calculate_probability(new_winner_points, new_loser_points, probabilities)
                else:
                    total_probability += (1.0 - probabilities[i]) * calculate_probability(new_winner_points, new_loser_points, probabilities)
        return total_probability

    probability_of_winning = calculate_probability(n, m, probabilities)
    print(f"{probability_of_winning:.6f}")

