    p = int(input())
    masters = []
    for _ in range(p):
        masters.append(tuple(map(int, input().split())))

    def manhattan_distance(start, end):
        return abs(start[0] - end[0]) + abs(start[1] - end[1])

    def is_safe(start, end):
        dist = manhattan_distance(start, end)
        for master in masters:
            if manhattan_distance(start, master) <= dist:
                if start[0] == master[0] and start[1] == master[1]:
                    continue
                if abs(start[0] - master[0]) + abs(start[1] - master[1]) <= dist:
                    return False
        return True

    def find_path(start, end):
        queue = [(start, 0)]
        visited = {start}

        while queue:
            curr, dist = queue.pop(0)

            if curr == end:
                return dist

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                next_x, next_y = curr[0] + dx, curr[1] + dy
                next_pos = (next_x, next_y)

                if 1 <= next_x <= r and 1 <= next_y <= c and next_pos not in visited and board[next_x-1][next_y-1] == '.':
                    visited.add(next_pos)
                    queue.append((next_pos, dist + 1))

        return float('inf')

    board = []
    for _ in range(r):
        board.append(input())

    min_turns = float('inf')

    for i in range(1 << (r * c)):
        temp_board = [row[:] for row in board]
        for j in range(r * c):
            if (i >> j) & 1:
                x = j // c + 1
                y = j % c + 1
                temp_board[x-1][y-1] = '#'

        possible = True
        for master in masters:
            if temp_board[master[0]-1][master[1]-1] == '.':
                possible = False
                break

        if possible:
            path_to_bed = find_path((1, 1), start_bed)
            path_to_fridge = find_path((1, 1), fridge)

            if path_to_bed != float('inf') and path_to_fridge != float('inf'):
                total_turns = path_to_bed + path_to_fridge
                min_turns = min(min_turns, total_turns)

    if min_turns == float('inf'):
        print("IMPOSSIBLE")
    else:
        print(min_turns)

