    dictionary = []
    for _ in range(n):
        dictionary.append(input())

    start = input()
    end = input()

    def get_neighbors(word, dictionary):
        neighbors = []
        for i in range(len(word)):
            for char_code in range(ord('A'), ord('Z') + 1):
                neighbor = list(word)
                neighbor[i] = chr(char_code)
                neighbor_word = "".join(neighbor)
                if neighbor_word in dictionary:
                    neighbors.append(neighbor_word)
        return neighbors

    def find_shortest_path(start, end, dictionary):
        queue = [(start, 0)]
        visited = {start}

        while queue:
            current_word, steps = queue.pop(0)

            if current_word == end:
                return steps

            neighbors = get_neighbors(current_word, dictionary)
            for neighbor in neighbors:
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append((neighbor, steps + 1))

        return float('inf')

    def find_best_word(dictionary, start, end):
        best_word = ""
        min_steps = float('inf')

        for word in dictionary:
            new_dictionary = dictionary.copy()
            new_dictionary.remove(word)

            steps = find_shortest_path(start, end, new_dictionary)
            if steps < min_steps:
                min_steps = steps
                best_word = word

        if min_steps == float('inf'):
            return "0 -1"
        else:
            return f"{best_word} {min_steps}"

    result = find_best_word(dictionary, start, end)
    print(result)

