            neighbors.append((r + 1, c))
        if c > 0:
            neighbors.append((r, c - 1))
        if c < w - 1:
            neighbors.append((r, c + 1))
        return neighbors

    def bfs(start_row, start_col, end_row, end_col):
        queue = [(start_row, start_col, 0)]
        visited = set()
        visited.add((start_row, start_col))

        while queue:
            r, c, dist = queue.pop(0)
            if r == end_row and c == end_col:
                return dist

