    def factorize(s):
        if not s:
            return []

        factors = []
        i = 0
        while i < n:
            j = i
            while j < n and s[j] == s[i]:
                j += 1

            factors.append(s[i:j])
            i = j

        return factors

    def get_weight(factors):
        weight = 0
        for factor in factors:
            weight += len(factor)
        return weight

    def is_irreducible(factors):
        for i in range(len(factors) - 1):
            if factors[i][-1] == '(' and factors[i+1][0] == '(':
                return False
        return True

    def find_maximal_factorizations(factors):
        if not factors:
            return []

        if is_irreducible(factors):
            return [factors]

        res = []
        for i in range(len(factors)):
            remaining_factors = factors[:i] + factors[i+1:]
            for sub_factorization in find_maximal_factorizations(remaining_factors):
                res.append([factors[i]] + sub_factorization)
        return res

    all_factorizations = []
    for i in range(1, n + 1):
        for factors in itertools.combinations(factorize(s), i):
            all_factorizations.append(list(factors))

    minimal_weight = float('inf')
    for factors in all_factorizations:
        for mf in find_maximal_factorizations(factors):
            minimal_weight = min(minimal_weight, get_weight(mf))

    print(minimal_weight)

