from collections import deque

def solve():
    r, c = map(int, input().split())
    grid = [['.' for _ in range(c)] for _ in range(r)]

    def is_valid(x, y):
        return 0 <= x < r and 0 <= y < c

    def get_neighbors(x, y):
        neighbors = []
        if is_valid(x - 1, y):
            neighbors.append((x - 1, y))
        if is_valid(x + 1, y):
            neighbors.append((x + 1, y))
        if is_valid(x, y - 1):
            neighbors.append((x, y - 1))
        if is_valid(x, y + 1):
            neighbors.append((x, y + 1))
        return neighbors

    def dfs(x, y, visited, grid, path):
        visited.add((x, y))
        path.append((x, y))

        count = 0
        for nx, ny in get_neighbors(x, y):
            if grid[nx][ny] == 'x' and (nx, ny) not in visited:
                count += dfs(nx, ny, visited, grid, path)

        if all(grid[nx][ny] == 'x' for nx, ny in get_neighbors(x, y) if (nx, ny) not in visited):
            return 1 + count
        else:
            return count

    def count_polygons():
        count = 0
        for i in range(r):
            for j in range(c):
                if grid[i][j] == '.':
                    visited = set()
                    path = []
                    grid[i][j] = 'x'
                    count += dfs(i, j, visited, grid, path)
                    grid[i][j] = '.'
        return count

    def count_ways_to_fill_remaining(r, c):
        if r == 1 and c == 1:
            return 1

        if r == 1:
            return 2 * c

        if c == 1:
            return 2 * r

        total_ways = 0

        # Fill the top row
        for i in range(1 << c):
            temp_grid = [['.' for _ in range(c)] for _ in range(r)]
            for k in range(c):
                if (i >> k) & 1:
                    temp_grid[0][k] = 'x'
            total_ways += count_polygons()

        # Fill the left column
        for i in range(1 << r):
            temp_grid = [['.' for _ in range(c)] for _ in range(r)]
            for k in range(r):
                if (i >> k) & 1:
                    temp_grid[k][0] = 'x'
            total_ways += count_polygons()

        # Fill the top-left 2x2 block
        if r >= 2 and c >= 2:
            temp_grid = [['.' for _ in range(c)] for _ in range(r)]
            temp_grid[0][0] = 'x'
            temp_grid[0][1] = 'x'
            temp_grid[1][0] = 'x'
            temp_grid[1][1] = 'x'
            total_ways += count_polygons()

        return total_ways

    print(count_ways_to_fill_remaining(r, c))

solve()

