    dp = {}

    def calculate_xor(current_x, current_y, current_xor):
        if (current_x, current_y, current_xor) in dp:
            return dp[(current_x, current_y, current_xor)]

        if current_x == n - 1 and current_y == m - 1:
            if current_xor == k:
                return 1
            else:
                return 0

        paths = 0

        # Move right
        if current_y + 1 < m:
            paths += calculate_xor(current_x, current_y + 1, current_xor ^ a[current_x][current_y])

        # Move down
        if current_x + 1 < n:
            paths += calculate_xor(current_x + 1, current_y, current_xor ^ a[current_x][current_y])

        dp[(current_x, current_y, current_xor)] = paths
        return paths

    print(calculate_xor(0, 0, 0))

