    umbrellas = []
    for _ in range(m):
        umbrellas.append(tuple(map(int, input().split())))

    def check(start, end):
        dp = [float('inf')] * (end + 2)
        dp[start] = 0

        for i in range(start, end + 1):
            for l, r in rain:
                if l <= i and i + 1 <= r:
                    for j in range(start, i + 2):
                        if dp[j] != float('inf'):
                            dp[i + 1] = min(dp[i + 1], dp[j] + umbrellas[i - j]..1)

        return dp[end] != float('inf')

    def find_min_fatigue():
        for i in range(1 << m):
            total_fatigue = 0
            umbrellas_used = []
            for j in range(m):
                if (i >> j) & 1:
                    total_fatigue += umbrellas[j][1]
                    umbrellas_used.append(umbrellas[j][1])

