def solve():
    n, k = map(int, input().split())
    s = input()

    if k > n // 2:
        print(-1)
        return

    ans = 0
    for i in range(1, 1 << n):
        subsequence = ""
        for j in range(n):
            if (i >> j) & 1:
                subsequence += s[j]

        length = len(subsequence)
        cost = n - length
        ans += cost

        if len(set(subsequence)) == len(subsequence) and len(subsequence) <= n // 2:
          k -= 1

        if k == 0:
          break

    if k > 0:
      print(-1)
    else:
      print(ans)

solve()

def solve():
    n = int(input())
    ans = 1
    while True:
        good = True
        temp = ans
        powers_of_3 = []
        power = 1
        while temp > 0:
            if temp % 3 == 0:
                power *= 3
            elif temp % 3 == 1:
                powers_of_3.append(power)
                power *= 3
            else:
                good = False
                break
            temp //= 3

        if good:
            if max(powers_of_3) < n:
                ans *= 3
            else:
                break
        else:
            ans *= 3
    print(ans)

