    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    def calculate_max_min(root, graph, visited, max_val, min_val):
        visited[root] = True

        for child in graph[root]:
            if not visited[child]:
                calculate_max_min(child, graph, visited, max_val, min_val)

        if root in graph:
            for leaf in graph[root]:
                max_val[0] = max(max_val[0], leaf)
                min_val[0] = min(min_val[0], leaf)
        else:
            max_val[0] = max(max_val[0], root)
            min_val[0] = min(min_val[0], root)

    def calculate_max_min_reverse(root, graph, visited, max_val, min_val):
        visited[root] = True

        for child in graph[root]:
            if not visited[child]:
                calculate_max_min_reverse(child, graph, visited, max_val, min_val)

        if root in graph:
            for leaf in graph[root]:
                max_val[1] = max(max_val[1], leaf)
                min_val[1] = min(min_val[1], leaf)
        else:
            max_val[1] = max(max_val[1], root)
            min_val[1] = min(min_val[1], root)

    visited = [False] * (n + 1)
    max_val = [0]
    min_val = [0]
