def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def solve():
    n, m = map(int, input().split())
    matrix = []
    for _ in range(n):
        matrix.append(list(map(int, input().split())))

    def check_prime_row(row):
        for x in row:
            if not is_prime(x):
                return False
        return True

    def check_prime_col(col):
        for i in range(n):
            if not is_prime(matrix[i][col]):
                return False
        return True

    def get_moves_to_prime_row(row):
        moves = 0
        for i in range(len(row)):
            if not is_prime(row[i]):
                moves += 1
        return moves

    def get_moves_to_prime_col(col):
        moves = 0
        for i in range(n):
            if not is_prime(matrix[i][col]):
                moves += 1
        return moves

    moves = 0
    for i in range(n):
        if check_prime_row(matrix[i]):
            continue
        moves += get_moves_to_prime_row(matrix[i])

    for j in range(m):
        if check_prime_col(j):
            continue
        moves += get_moves_to_prime_col(j)

    if moves == 0:
        print(0)
    else:
        print(moves)

