def solve():
    n, k = map(int, input().split())

    mod = 10**6 + 3

    def binom(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        res = 1
        for i in range(k):
            res = (res * (n - i)) % mod
            res = (res * pow(i + 1, mod - 2, mod)) % mod
        return res

    def calculate_probability(n, k):
        total_ways = pow(2**n, k, mod)
        favorable_ways = 0

        for i in range(1, k + 1):
            favorable_ways = (favorable_ways + binom(k, i) * pow(2**n - 1, k - i, mod)) % mod

        return (total_ways - favorable_ways) % mod

    probability = calculate_probability(n, k)

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def solve_gcd(a, b):
        if a == 0:
            return b
        if b == 0:
            return a
        if a == b:
            return a
        if a > b:
            return solve_gcd(a - b, b)
        return solve_gcd(a, b - a)

    a = probability
    b = pow(2**n, k, mod)

    common_divisor = solve_gcd(a, b)

    a //= common_divisor
    b //= common_divisor

    print(a, b)

