        if i not in p:
            for j in range(i + 1, n + 1):
                if j not in p:
                    dist = bfs(i, j)
                    if dist <= d:
                        possible.add(i)
                        possible.add(j)

    count = 0
    for i in range(1, n + 1):
        if i in possible:
            count += 1

    print(count)

