    def count_paths(start, end):
        visited = [[False for _ in range(n)] for _ in range(n)]
        stack = [start]
        paths = 0

        while stack:
            x, y = stack.pop()

            if (x, y) == end:
                paths += 1
                continue

            if visited[x][y]:
                continue

            visited[x][y] = True

            if is_valid(x + 1, y):
                stack.append((x + 1, y))
            if is_valid(x - 1, y):
                stack.append((x - 1, y))
            if is_valid(x, y + 1):
                stack.append((x, y + 1))
            if is_valid(x, y - 1):
                stack.append((x, y - 1))

        return paths

    paths_right_down = count_paths(s, t)

    possible_up_left = False
    for i in range(n):
        if is_valid(s[0], i) and is_valid(i, s[1]):
            possible_up_left = True
            break

    if not possible_up_left:
        print("INCONCEIVABLE")
    elif paths_right_down == 0:
        print("THE GAME IS A LIE")
