        for i in range(len(division) - 1):
            if int(division[i]) >= int(division[i+1]):
                return False
        return True

    def find_divisions(s):
        n = len(s)
        for i in range(1, n):
            for j in range(i + 1, n):
                first_part = s[:i]
                second_part = s[i:j]
                third_part = s[j:]

                if check_division([first_part, second_part, third_part]):
                    return [first_part, second_part, third_part]
                if check_division([first_part, third_part, second_part]):
                    return [first_part, third_part, second_part]
                if check_division([second_part, first_part, third_part]):
                    return [second_part, first_part, third_part]
                if check_division([second_part, third_part, first_part]):
                    return [second_part, third_part, first_part]
                if check_division([third_part, first_part, second_part]):
                    return [third_part, first_part, second_part]
                if check_division([third_part, second_part, first_part]):
                    return [third_part, second_part, first_part]
        return None

    division = find_divisions(s)
    if division:
        print("YES")
