    def calculate_value(arr):
        val = arr[0]
        for i in range(1, len(arr)):
            val = f(val, arr[i])
        return val

    def reorder_array(arr):
        best_order = arr[:]
        max_value = calculate_value(arr)

        for i in range(len(arr)):
            for j in range(len(arr)):
                temp_arr = arr[:]
                temp_arr[i], temp_arr[j] = temp_arr[j], temp_arr[i]
                temp_value = calculate_value(temp_arr)
                if temp_value > max_value:
                    max_value = temp_value
                    best_order = temp_arr[:]

        return best_order

    result = reorder_array(a)
    print(*result)

