    n = int(input())
    m = int(input())

    MOD = 10**9 + 7

    def pow_mod(a, b, m):
        res = 1
        a %= m
        while b > 0:
            if b % 2 == 1:
                res = (res * a) % m
            a = (a * a) % m
            b //= 2
        return res

    def inv_mod(a, m):
        return pow_mod(a, m - 2, m)

    def comb(n, k, m):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        res = 1
        for i in range(k):
            res = (res * (n - i)) % m
            res = (res * inv_mod(i + 1, m)) % m
        return res

    def solve_mod(n, m):
        if n == 0:
            return 1
        if m == 1:
            return 0

        dp = [[0] * (m + 1) for _ in range(n + 1)]
        dp[0][0] = 1

        for i in range(1, n + 1):
            for j in range(m + 1):
                if j >= i:
                    dp[i][j] = (dp[i][j - i] + dp[i - 1][j]) % MOD
                else:
                    dp[i][j] = dp[i - 1][j]

        return dp[n][m]

    print(solve_mod(n, m))

