        for i in range(start, end):
            a[i] = 10**10 if a[i] < 0 else -10**10

    def swap(k):
        nonlocal a
        start1 = (2 * k - 2) * (1 << k)
        end1 = (2 * k - 1) * (1 << k)
        start2 = (2 * k - 1) * (1 << k)
        end2 = 2 * k * (1 << k)
        for i in range(start1, end1):
            a[i], a[i + (end2 - start2)] = a[i + (end2 - start2)], a[i]

    for _ in range(q):
