        config_you.append(list(map(int, input().split())))

    def get_neighbors(room, config):
        neighbors = []
        if config[room-1][0] != 0:
            neighbors.append(config[room-1][0])
        if config[room-1][1] != 0:
            neighbors.append(config[room-1][1])
        if config[room-1][2] != 0:
            neighbors.append(config[room-1][2])
        if config[room-1][3] != 0:
            neighbors.append(config[room-1][3])
        return neighbors

    def is_valid_path(path, config):
        current_room = 1
        for move in path:
            neighbors = get_neighbors(current_room, config)
            if move not in neighbors:
                return False
            current_room = neighbors.index(move) + 1
        return current_room == n

    def find_all_paths(start_room, end_room, config, path, paths):
        if start_room == end_room:
            paths.append(path[:])
            return

        neighbors = get_neighbors(start_room, config)
        for neighbor in neighbors:
            path.append(neighbor)
            find_all_paths(neighbor, end_room, config, path, paths)
            path.pop()

    paths_senior = []
    find_all_paths(1, n, config_senior, [1], paths_senior)

    paths_you = []
    find_all_paths(1, n, config_you, [1], paths_you)

    for path in paths_you:
        if is_valid_path(path, config_senior):
            print("Yes")
            return

    print("No")

