def solve():
    n = int(input())
    a = list(map(int, input().split()))

    MOD = 998244353

    def power(a, b, m):
        res = 1
        a = a % m
        while b > 0:
            if b % 2 == 1:
                res = (res * a) % m
            a = (a * a) % m
            b //= 2
        return res

    def binomial_coefficient(n, k, m):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        res = 1
        for i in range(k):
            res = (res * (n - i)) % m
            res = (res * power(i + 1, m - 2, m)) % m
        return res

    def calculate_difficulty(distribution):
        total_difficulty = 0
        for i in range(n - 1):
            if distribution[i] == 1:
                total_difficulty += a[i + 1]
        return total_difficulty

    total_difficulties = []
    for i in range(1 << (n - 1)):
        distribution = []
        for j in range(n - 1):
            if (i >> j) & 1:
                distribution.append(1)
            else:
                distribution.append(0)

        total_difficulties.append(calculate_difficulty(distribution))

    total_difficulties_sum = sum(total_difficulties)
    expected_value = total_difficulties_sum / len(total_difficulties)

    p = expected_value * power(2, n - 1, MOD)
    print(int(p % MOD))

