    edges = []
    for _ in range(m):
        a, b, c = map(int, input().split())
        edges.append((a, b, c))

    def solve():
        dist = [float('inf')] * (n + 1)
        dist[1] = 0

        q = []
        heapq.heappush(q, (0, 1))

        while q:
            d, u = heapq.heappop(q)

            if d > dist[u]:
                continue

            for v, c in edges:
                if v == u:
                    continue

                if dist[v] > dist[u] + c:
                    dist[v] = dist[u] + c
                    heapq.heappush(q, (dist[v], v))

        return dist[n]

    def check(max_score):
        dist = [float('inf')] * (n + 1)
        dist[1] = 0

        q = []
        heapq.heappush(q, (0, 1))

        while q:
            d, u = heapq.heappop(q)

            if d > dist[u]:
                continue

            for v, c in edges:
                if v == u:
                    continue

                if dist[v] > dist[u] + c:
                    dist[v] = dist[u] + c
                    heapq.heappush(q, (dist[v], v))

        if dist[n] > max_score:
            return False
        else:
            return True

    low = 0
    high = float('inf')

    while low <= high:
        mid = (low + high) // 2

        if check(mid):
            low = mid + 1
