    p, m = map(int, input().split())

    def calculate(expression):
        stack = []
        num = ""
        for char in expression:
            if char.isdigit():
                num += char
            else:
                if num:
                    stack.append(int(num))
                    num = ""
                if char == '(':
                    stack.append(char)
                elif char == ')':
                    sub_sum = 0
                    while stack[-1] != '(':
                        sub_sum += stack.pop()
                    stack.pop()
                    stack.append(sub_sum)
                else:
                    if stack and stack[-1] == '+':
                        stack.pop()
                        stack.append(stack.pop() + stack.pop())
                    elif stack and stack[-1] == '-':
                        stack.pop()
                        right = stack.pop()
                        left = stack.pop()
                        stack.append(left - right)
        if num:
            stack.append(int(num))
        while len(stack) > 1:
            stack.append(stack.pop() + stack.pop())
        return stack[0]

    def find_max_value(p, m):
        max_val = 0
        for i in range(2**p):
            ops = []
            for j in range(p):
                if (i >> j) & 1:
                    ops.append('+')
                else:
                    ops.append('-')

            expression = e.replace('?', '0')
            for j in range(m):
                expression = expression.replace('?', '0')

            for perm in permutations(ops):
                temp_expression = expression
                for k in range(p):
                    temp_expression = temp_expression.replace('?', str(perm[k]))

                for k in range(m):
                    temp_expression = temp_expression.replace('?', str(perm[k]))

                val = calculate(temp_expression)
                max_val = max(max_val, val)
        return max_val

    def find_min_value(p, m):
        min_val = float('inf')
        for i in range(2**p):
            ops = []
            for j in range(p):
                if (i >> j) & 1:
                    ops.append('+')
                else:
                    ops.append('-')

            expression = e.replace('?', '0')
            for j in range(m):
                expression = expression.replace('?', '0')

            for perm in permutations(ops):
                temp_expression = expression
                for k in range(p):
                    temp_expression = temp_expression.replace('?', str(perm[k]))

                for k in range(m):
                    temp_expression = temp_expression.replace('?', str(perm[k]))

                val = calculate(temp_expression)
                min_val = min(min_val, val)
        return min_val

    max_val = find_max_value(p, m)
    min_val = find_min_value(p, m)

