    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    def find_path(start, end):
        visited = [False] * (n + 1)
        stack = [(start, [start])]
        while stack:
            node, path = stack.pop()
            if node == end:
                return path
            visited[node] = True
            for neighbor in range(1, n + 1):
                if not visited[neighbor] and (node, neighbor) in edges:
                    stack.append((neighbor, path + [neighbor]))
        return None

    max_common = 0
    max_path1 = None
    max_path2 = None

    for i in range(n):
        for j in range(i + 1, n):
            path1 = find_path(i + 1, j + 1)
            path2 = find_path(j + 1, i + 1)
            common = 0
            for node in path1:
                if node in path2:
                    common += 1
            if common > max_common:
                max_common = common
                max_path1 = path1
                max_path2 = path2

    print(*max_path1, *max_path2)

