    def find_path(start, end, visited, graph):
        if start == end:
            return True
        visited.add(start)
        for neighbor in graph[start]:
            if neighbor not in visited:
                if find_path(neighbor, end, visited, graph):
                    return True
        return False

    def calculate_cost(assignments, routes):
        graph = {}
        for u, v, c in routes:
            if u not in graph:
                graph[u] = []
            if v not in graph:
                graph[v] = []
            graph[u].append(v)
            graph[v].append(u)

        total_cost = 0
        for u, v in assignments:
            if u not in graph or v not in graph:
                continue
            if find_path(u, v, set(), graph):
                total_cost += graph[u].index(v)
        return total_cost

    min_cost = float('inf')
    for i in range(1 << (2**n)):
        current_assignment = []
        for j in range(2**n):
            if (i >> j) & 1:
                current_assignment.append(cities[j])

        current_cost = calculate_cost(current_assignment, routes)
        min_cost = min(min_cost, current_cost)

    print(min_cost)

