    n = int(input())
    counts = list(map(int, input().split()))
    forbidden = list(map(int, input().split()))
    sequence = list(map(int, input().split()))

    MOD = 1000000007

    def factorial(n):
        if n == 0:
            return 1
        result = 1
        for i in range(1, n + 1):
            result = (result * i) % MOD
        return result

    def combinations(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        result = 1
        for i in range(k):
            result = (result * (n - i)) % MOD
            result = (result * pow(i + 1, MOD - 2, MOD)) % MOD
        return result

    def count_arrangements(arrangement):
        count = 1
        for color in range(n):
            count = (count * combinations(arrangement.count(color), counts[color])) % MOD
        return count

    def find_valid_arrangements(arrangement):
        valid = True
        for color in forbidden:
            if arrangement.count(color) > 0:
                valid = False
                break
        if not valid:
            return 0

        sequence_count = 0
        i = 0
        while i < len(arrangement):
            found_sequence = True
            for j in range(len(sequence)):
                if arrangement[i + j] != sequence[j]:
                    found_sequence = False
                    break
            if found_sequence:
                sequence_count += 1
                i += len(sequence)
            else:
                i += 1

        return sequence_count

    def generate_arrangements(current_arrangement, remaining_colors):
        if len(current_arrangement) == n:
            count = find_valid_arrangements(current_arrangement)
            if count > 0:
                return count
            else:
                return 0

        total_count = 0
        for color in remaining_colors:
            new_arrangement = current_arrangement + [color]
            new_remaining_colors = [c for c in remaining_colors if c != color]
            total_count = (total_count + generate_arrangements(new_arrangement, new_remaining_colors)) % MOD
        return total_count

    total_arrangements = 0
    for i in range(1 << n):
        arrangement = []
        remaining_colors = list(range(n))
        for j in range(n):
            if (i >> j) & 1:
                arrangement.append(j)
                remaining_colors.remove(j)

        total_arrangements = (total_arrangements + generate_arrangements(arrangement, remaining_colors)) % MOD

