def solve():
    n = int(input())
    positions = [input() for _ in range(n)]

    def find_min_rounds(start, end):
        if start == end:
            return 0

        options = []
        for i in range(n):
            if positions[i][0] == start:
                options.append(positions[i][1:])

        if not options:
            return -1

        min_rounds = float('inf')
        for option in options:
            for next_pos in option.split():
                rounds = find_min_rounds(next_pos, end)
                if rounds != -1:
                    min_rounds = min(min_rounds, rounds + 1)

        return min_rounds if min_rounds != float('inf') else -1

    for i in range(n):
        for j in range(i + 1, n):
            start = positions[i][0]
            end = positions[j][0]
            print(find_min_rounds(start, end), end=" ")
        print()

solve()

