def solve():
    n, d = map(int, input().split())
    a = list(map(int, input().split()))
    m = int(input())

    fares = []
    for _ in range(m):
        s, d, t, p = input().split()
        fares.append((int(s), int(d), t, int(p)))

    dp = {}

    def calculate_cost(start, end, remaining_tickets, path):
        if (start, end, remaining_tickets, tuple(path)) in dp:
            return dp[(start, end, remaining_tickets, tuple(path))]

        if remaining_tickets == 0:
            if start == end:
                return 0
            else:
                return float('inf')

        if start == end:
            if remaining_tickets > 0:
                return float('inf')
            else:
                return 0

        min_cost = float('inf')

        for s, d, t, p in fares:
            if start == s and d == end:
                if t == 'O':
                    new_cost = p + calculate_cost(start, end, remaining_tickets - 1, path + [d])
                    min_cost = min(min_cost, new_cost)
                elif t == 'R':
                    new_cost = p + calculate_cost(start, end, remaining_tickets - 2, path + [d])
                    min_cost = min(min_cost, new_cost)

        dp[(start, end, remaining_tickets, tuple(path))] = min_cost
        return min_cost

    print(calculate_cost(a[0], a[-1], d, [a[0]]))

solve()

