def solve():
    n, m, alpha = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v, c = map(int, input().split())
        edges.append((u, v, c))

    def calculate_energy(path):
        max_candies = 0
        num_roads = len(path)
        for i in range(num_roads):
            max_candies = max(max_candies, edges[path[i]][2])
        return max_candies**2 + alpha * num_roads

    def find_path(start, end, visited):
        if start == end:
            return [start]

        visited[start] = True
        for i in range(m):
            u, v, _ = edges[i]
            if u == start and not visited[v]:
                path = find_path(v, end, visited)
                if path:
                    return [start] + path
            elif v == start and not visited[u]:
                path = find_path(u, end, visited)
                if path:
                    return [start] + path
        return None

    min_energy = float('inf')
    for start in range(1, n + 1):
        for end in range(1, n + 1):
            if start != end:
                visited = [False] * (n + 1)
                path = find_path(start, end, visited)
                if path:
                    min_energy = min(min_energy, calculate_energy(path))

    if min_energy == float('inf'):
        print("Poor girl")
    else:
        print(min_energy)

