def solve():
    x = int(input())

    def binom(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        res = 1
        for i in range(k):
            res = res * (n - i) // (i + 1)
        return res

    def find_n_k(x):
        for n in range(1, 10000000000):
            for k in range(1, n + 1):
                if binom(n, k) == x:
                    return n, k
        return None, None

    n, k = find_n_k(x)
    print(n, k)

