def solve():
    n = int(input())
    nums = []
    for _ in range(n):
        nums.append(int(input()))

    MOD = 10**9 + 7

    def count_ways(arr):
        n = len(arr)
        dp = [[0] * n for _ in range(n)]

        for i in range(n):
            dp[i][i] = 1

        for length in range(2, n + 1):
            for i in range(n - length + 1):
                j = i + length - 1
                for k in range(i, j):
                    if gcd(arr[i], arr[k]) > 1:
                        dp[i][j] = (dp[i][j] + dp[i][k] * dp[k+1][j]) % MOD

        return dp[0][n-1]

    print(count_ways(nums))

