def solve():
    n, m = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(list(map(int, input().split())))

    def calculate_cost(grid):
        cost = 0
        for i in range(n):
            for j in range(m):
                cost += abs(grid[i][j] - grid[i][j])
        return cost

    def get_moves(grid):
        moves = []
        for i in range(n):
            moves.append((i, "row"))
        for j in range(m):
            moves.append((j, "col"))
        return moves

    def is_valid_move(grid, row_or_col, value):
        for i in range(n):
            if row_or_col == i:
                if grid[i][value] != 1:
                    return False
            else:
                if grid[row_or_col][i] != 1:
                    return False
        return True

    def find_optimal_moves(grid):
        moves = get_moves(grid)
        best_cost = float('inf')
        best_moves = []

        def backtrack(current_grid, current_moves):
            nonlocal best_cost, best_moves
            cost = calculate_cost(current_grid)
            if cost < best_cost:
                best_cost = cost
                best_moves = current_moves[:]

            for row_or_col, move_type in moves:
                if move_type == "row":
                    new_grid = [row[:] for row in current_grid]
                    for i in range(m):
                        new_grid[row_or_col][i] += 1
                    if is_valid_move(new_grid, row_or_col, i):
                        backtrack(new_grid, current_moves + [(row_or_col, move_type)])
                else:
                    new_grid = [row[:] for row in current_grid]
                    for j in range(n):
                        new_grid[j][row_or_col] += 1
                    if is_valid_move(new_grid, row_or_col, j):
                        backtrack(new_grid, current_moves + [(row_or_col, move_type)])

        backtrack(grid, [])
        return best_moves

    moves = find_optimal_moves(grid)

    if moves is None:
        print(-1)
        return

    print(len(moves))
    for move in moves:
        print(move[1], move[0] + 1)

solve()

