    def find_shortest_path(start, end, dictionary):
        from collections import deque
        queue = deque([(start, 0)])
        visited = {start}

        while queue:
            current, steps = queue.popleft()

            if current == end:
                return steps

            for i in range(len(current)):
                for char_code in range(ord('A'), ord('Z') + 1):
                    new_char = chr(char_code)
                    if new_char != current[i]:
                        neighbor = current[:i] + new_char + current[i+1:]
                        if neighbor in dictionary and neighbor not in visited:
                            visited.add(neighbor)
                            queue.append((neighbor, steps + 1))

        return float('inf')

    shortest_path = float('inf')
    best_word = ""

    for word in dictionary:
        new_word = word
        new_steps = find_shortest_path(start, end, dictionary + [new_word])
        if new_steps < shortest_path:
            shortest_path = new_steps
            best_word = new_word

    if shortest_path == float('inf'):
        print("0")
        print("-1")
    else:
        print(best_word)
        print(shortest_path)

