def solve():
    h, w = map(int, input().split())
    grid = []
    for _ in range(h):
        grid.append(list(input()))

    s_row = -1
    s_col = -1
    t_row = -1
    t_col = -1

    for i in range(h):
        for j in range(w):
            if grid[i][j] == 'S':
                s_row = i
                s_col = j
            elif grid[i][j] == 'T':
                t_row = i
                t_col = j

    leaves_to_remove = 0

    def is_valid(x, y):
        return 0 <= x < h and 0 <= y < w

    def get_neighbors(x, y):
        neighbors = []
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if is_valid(nx, ny):
                neighbors.append((nx, ny))
        return neighbors

    def bfs(start_row, start_col):
        queue = [(start_row, start_col)]
        visited = set()
        visited.add((start_row, start_col))

        while queue:
            row, col = queue.pop(0)
            if (row, col) == (t_row, t_col):
                return True

            neighbors = get_neighbors(row, col)
            for neighbor_row, neighbor_col in neighbors:
                if (neighbor_row, neighbor_col) not in visited and grid[neighbor_row][neighbor_col] != '.':
                    visited.add((neighbor_row, neighbor_col))
                    queue.append((neighbor_row, neighbor_col))
        return False

    def remove_leaves():
        for i in range(h):
            for j in range(w):
                if grid[i][j] != '.' and grid[i][j] != 'S' and grid[i][j] != 'T':
                    grid[i][j] = '.'
                    if not bfs(s_row, s_col):
                        grid[i][j] = 'o'
                        leaves_to_remove += 1
                    else:
                        grid[i][j] = 'o'

    remove_leaves()
    if bfs(s_row, s_col):
        print(leaves_to_remove)
    else:
        print(-1)

solve()

