def solve():
    s = input()
    n = len(s)

    def factorize(s):
        if not s:
            return []

        factors = []
        i = 0
        while i < n:
            j = i
            while j < n and s[j] == s[i]:
                j += 1
            factors.append((s[i:j], j - i))
            i = j

        return factors

    def get_weight(factors):
        weight = 0
        for factor, _ in factors:
            weight += len(factor)
        return weight

    def is_irreducible(factors):
        for factor, count in factors:
            if '(' in factor or ')' in factor:
                return False
            for i in range(len(factor) - 1):
                if factor[i] == factor[i+1]:
                    return False
        return True

    def find_maximal_factorization(factors):
        if not factors:
            return []

        if is_irreducible(factors):
            return factors

        best_factors = []
        for i in range(len(factors)):
            remaining_factors = factors[:i] + factors[i+1:]
            for sub_factor, _ in factors[i]:
                sub_factors = factorize(sub_factor)
                remaining_factors_with_sub = remaining_factors[:]
                remaining_factors_with_sub.append(sub_factors)

                sub_factor_weight = get_weight(sub_factors)
                remaining_weight = get_weight(remaining_factors_with_sub)

                new_weight = sub_factor_weight + remaining_weight

                if not best_factors or new_weight < get_weight(best_factors):
                    best_factors = [factors[i]] + find_maximal_factorization(remaining_factors_with_sub)

        return best_factors

    factors = factorize(s)
    maximal_factors = find_maximal_factorization(factors)

    print(get_weight(maximal_factors))

solve()

