def solve():
    n, m = map(int, input().split())
    roads = []
    for _ in range(m):
        roads.append(list(map(int, input().split())))

    adj = [[] for _ in range(n + 1)]
    for o, d, l in roads:
        adj[o].append((d, l))

    def shortest_paths(node, target, visited, path_length):
        if node == target:
            return 1

        visited[node] = True

        count = 0
        for neighbor, length in adj[node]:
            if not visited[neighbor]:
                new_path_length = path_length + length
                count += shortest_paths(neighbor, target, visited[:], new_path_length)

        visited[node] = False
        return count

    for o, d, l in roads:
        visited = [False] * (n + 1)
        shortest_count = shortest_paths(o, d, visited, l)
        print(shortest_count % 1000000007)

solve()

