            adj[u].append(v)
            adj[v].append(u)

        def bfs(start):
            q = [(start, 0)]
            visited = {start}
            while q:
                node, dist = q.pop(0)
                if dist > k:
                    return False
                for neighbor in adj[node]:
                    if neighbor not in visited:
                        visited.add(neighbor)
                        q.append((neighbor, dist + 1))
            return True

        return bfs(1)

