    n = int(input())
    packages = []
    for _ in range(n):
        packages.append(tuple(map(int, input().split())))

    def calculate_moves(path):
        x, y = 0, 0
        moves = 0
        for move in path:
            if move == 'R':
                x += 1
            else:
                y += 1
            moves += 1
        return moves

    def find_lexicographically_smallest_path(packages):
        import itertools

        all_permutations = list(itertools.permutations(packages))

        best_path = None
        min_moves = float('inf')

        for perm in all_permutations:
            path = ''.join(['R' if x > y else 'U' for x, y in perm])
            moves = calculate_moves(path)
            if moves < min_moves:
                min_moves = moves
                best_path = path
            elif moves == min_moves and path < best_path:
                best_path = path

        return best_path

    path = find_lexicographically_smallest_path(packages)

    if path is None:
        print("NO")
    else:
        print("YES")
        print(path)

