    a = []
    for _ in range(n):
        a.append(list(map(int, input().split())))

    MOD = 10**9 + 7

    dp = {}

    def calculate(x, y, curr_xor):
        if (x, y, curr_xor) in dp:
            return dp[(x, y, curr_xor)]

        if x == n and y == m:
            if curr_xor == k:
                return 1
            else:
                return 0

        if x > n or y > m:
            return 0

        right_paths = calculate(x, y + 1, curr_xor ^ a[x][y])
        down_paths = calculate(x + 1, y, curr_xor ^ a[x][y])

        dp[(x, y, curr_xor)] = (right_paths + down_paths) % MOD
        return (right_paths + down_paths) % MOD

    print(calculate(1, 1, 0))

