def factorial_last_digit(n):
    if n <= 1:
        return 1
    elif n == 2:
        return 2
    elif n == 3:
        return 6
    else:
        last_digit = 1
        for i in range(4, n + 1):
            last_digit = (last_digit * (i % 10)) % 10
        return last_digit

