            if arr[i] > arr[i+1] > arr[i+2]:
                return True
        return False

    def rotate(arr, i):
        temp = arr[i:i+3]
        temp.reverse()
        arr[i:i+3] = temp

    def find_solution(arr, target):
        if arr == target:
            return True

        for i in range(n - 2):
            temp_arr = arr[:]
            rotate(temp_arr, i)
            if find_solution(temp_arr, target):
                return True
        return False

