def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True


def solve():
    n, m = map(int, input().split())
    matrix = []
    for _ in range(n):
        matrix.append(list(map(int, input().split())))

    def check_prime_row(row):
        for num in row:
            if not is_prime(num):
                return False
        return True

    def check_prime_col(col):
        for i in range(n):
            if not is_prime(matrix[i][col]):
                return False
        return True

    def get_prime_row(row):
        for i in range(len(row)):
            if not is_prime(row[i]):
                row[i] = 1
        return row

    def get_prime_col(col):
        for i in range(n):
            matrix[i][col] = 1
        return matrix

    def count_moves():
        moves = 0
        for i in range(n):
            if check_prime_row(matrix[i]):
                continue
            else:
                matrix[i] = get_prime_row(matrix[i])
                moves += 1

        for j in range(m):
            if check_prime_col(j):
                continue
            else:
                matrix = get_prime_col(j)
                moves += 1

        for i in range(n):
            for j in range(m):
                if not is_prime(matrix[i][j]):
                    return float('inf')

        return moves

    def check_prime(matrix):
        for i in range(n):
            if check_prime_row(matrix[i]):
                return True
        for j in range(m):
            if check_prime_col(j):
                return True
        return False

    if check_prime(matrix):
        print(0)
        return

    moves = count_moves()
    if moves == float('inf'):
        print(-1)
    else:
        print(moves)


