def solve():
    n, k = map(int, input().split())

    def binom(n, k):
        if k < 0 or k > n:
            return 0
        if k == 0 or k == n:
            return 1
        if k > n // 2:
            k = n - k
        res = 1
        for i in range(k):
            res = (res * (n - i)) % (10**6 + 3)
            res = (res * pow(i + 1, -1, 10**6 + 3)) % (10**6 + 3)
        return res

    def calculate_probability(n, k):
        total_ways = pow(2**n, k, 10**6 + 3)
        favorable_ways = 0
        for i in range(1, k + 1):
            favorable_ways = (favorable_ways + binom(k, i) *
                              pow(2**n - 1, k - i, 10**6 + 3)) % (10**6 + 3)
        probability = (favorable_ways * pow(2**n - 1, k, 10**6 + 3)) % (10**6 + 3)
        return probability

    probability = calculate_probability(n, k)
    gcd = bin_gcd(probability, 10**6 + 3)
    probability //= gcd
    print(probability, 10**6 + 3 // gcd)


def bin_gcd(a, b):
    while b:
        a, b = b, a % b
    return a


