def calculate_spaciousness(half):
    max_area = 0
    for i in range(len(half)):
        for j in range(i + 1, len(half)):
            x1, y1, r1 = half[i]
            x2, y2, r2 = half[j]

            intersection_area = calculate_intersection_area(x1, y1, r1,
                                                           x2, y2, r2)

            max_area = max(max_area, intersection_area)

    return max_area


def calculate_intersection_area(x1, y1, r1, x2, y2, r2):
    distance = math.sqrt((x1 - x2)**2 + (y1 - y2)**2)

    if distance > r1 + r2:
        return 0

    if distance <= abs(r1 - r2):
        return math.pi * min(r1, r2)**2

    if distance <= r1 and distance <= r2:
        return math.pi * distance**2

    if distance > r1 and distance > r2:
        return math.pi * r1**2 + math.pi * r2**2 - \
            calculate_circular_segment_area(r1, distance) - \
            calculate_circular_segment_area(r2, distance)

    if distance < r1 and distance < r2:
        return math.pi * r1**2 + math.pi * r2**2 - \
            calculate_circular_segment_area(r1, distance) - \
            calculate_circular_segment_area(r2, distance)


def calculate_circular_segment_area(radius, distance):
    if distance > radius:
        return 0
    elif distance == radius:
        return math.pi * radius**2 / 2
    else:
        angle = math.acos(distance / radius)
        return math.pi * radius**2 * (angle - math.sin(angle)) / 2


